					}
					 transition(edgeName="t03",targetState="timeout",cond=whenTimeout("local_tout_sonarlogic_stateOccupied"))   
					transition(edgeName="t04",targetState="applylogic",cond=whenEvent("localsonarupdate"))
				}	 
				state("timeout") { //this:State
